              <li className="list-disc">Report suspicious emails to your IT/security team</li>
              <li className="list-disc">Verify urgent requests through alternative channels</li>
              <li className="list-disc">Never click links or download attachments from unknown senders</li>
            </ul>
          </div>
        </div>
      )}

      <ToolStateActions
        onReset={reset}
        onCopy={copy_share_link}
        onExport={export_json}
        onImport={import_json}
      />
    </div>
  );
}

"use client";

import React, { useState, useCallback, useMemo } from "react";
import { MessageSquare, Send, Loader2, Sparkles } from "lucide-react";

interface NLPCommand {
  intent: string;
  confidence: number;
  parameters: Record<string, any>;
  action: string;
}

interface NLPInterfaceProps {
  onCommand?: (command: NLPCommand) => Promise<any>;
  placeholder?: string;
  showHistory?: boolean;
  className?: string;
}

const parseIntent = (input: string): NLPCommand | null => {
  const lower = input.toLowerCase().trim();

  // Simple intent parsing (would use NLP/ML in production)
  if (lower.includes("generate") || lower.includes("create")) {
    if (lower.includes("code") || lower.includes("function")) {
      return {
        intent: "generate-code",
        confidence: 0.8,
        parameters: { type: "code" },
        action: "generate"
      };
    }
    if (lower.includes("config") || lower.includes("configuration")) {
      return {
        intent: "generate-config",
        confidence: 0.8,
        parameters: { type: "config" },
        action: "generate"
      };
    }
  }

  if (lower.includes("process") || lower.includes("run") || lower.includes("execute")) {
    return {
      intent: "execute-workflow",
      confidence: 0.7,
      parameters: {},
      action: "execute"
    };
  }

  if (lower.includes("deploy") || lower.includes("publish")) {
    const target = lower.includes("github") ? "github"
      : lower.includes("aws") ? "aws"
      : lower.includes("azure") ? "azure"
      : "default";

    return {
      intent: "deploy",
      confidence: 0.9,
      parameters: { target },
      action: "deploy"
    };
  }

  if (lower.includes("validate") || lower.includes("check")) {
    return {
      intent: "validate",
      confidence: 0.8,
      parameters: {},
      action: "validate"
    };
  }

  if (lower.includes("help") || lower.includes("how") || lower.includes("what")) {
    return {
      intent: "help",
      confidence: 0.9,
      parameters: { query: input },
      action: "help"
    };
  }

  return null;
};

export function NLPInterface({
  onCommand,
  placeholder = "Type a command in natural language...",
  showHistory = true,
  className = ""
}: NLPInterfaceProps) {
  const [input, setInput] = useState("");
  const [processing, setProcessing] = useState(false);
  const [history, setHistory] = useState<Array<{ input: string; command: NLPCommand; result?: any }>>([]);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim() || processing) return;

    const command = parseIntent(input);
    if (!command) {
      setError("I didn't understand that command. Try: 'generate code', 'process data', 'deploy to github', etc.");
      return;
    }

    setProcessing(true);
    setError(null);

    try {
      let result;
      if (onCommand) {
        result = await onCommand(command);
      } else {
        // Default action simulation
        result = { success: true, message: `Executed: ${command.intent}` };
      }

      setHistory(prev => [...prev, { input, command, result }]);
      setInput("");
    } catch (err) {
      setError(err instanceof Error ? err.message : "Command failed");
    } finally {
      setProcessing(false);
    }
  }, [input, processing, onCommand]);

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <div className={`rounded-2xl border border-slate-200 bg-white p-6 shadow-sm ${className}`}>
      <div className="flex items-center gap-2 mb-4">
        <Sparkles className="w-5 h-5 text-sky-600" />
        <h3 className="text-lg font-semibold text-slate-900">Natural Language Interface</h3>
      </div>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="relative">
          <MessageSquare className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder={placeholder}
            disabled={processing}
            className="w-full pl-10 pr-12 py-3 rounded-lg border border-slate-200 focus:outline-none focus:ring-2 focus:ring-sky-500 disabled:opacity-50 disabled:cursor-not-allowed"
          />
          <button
            type="submit"
            disabled={processing || !input.trim()}
            className="absolute right-2 top-1/2 -translate-y-1/2 p-2 text-sky-600 hover:text-sky-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            aria-label="Send"
          >
            {processing ? (
              <Loader2 className="w-5 h-5 animate-spin" />
            ) : (
              <Send className="w-5 h-5" />
            )}
          </button>
        </div>

        {error && (
          <div className="p-3 rounded-lg bg-rose-50 border border-rose-200 text-sm text-rose-800">
            {error}
          </div>
        )}

        {/* Quick Commands */}
        <div className="flex flex-wrap gap-2">
          <span className="text-xs text-slate-500">Try:</span>
          {["Generate code", "Process data", "Deploy to GitHub", "Validate output"].map((cmd) => (
            <button
              key={cmd}
              type="button"
              onClick={() => setInput(cmd)}
              className="px-3 py-1 text-xs border border-slate-200 rounded-lg hover:bg-slate-50 transition-colors"
            >
              {cmd}
            </button>
          ))}
        </div>
      </form>

      {/* Command History */}
      {showHistory && history.length > 0 && (
        <div className="mt-6 space-y-2">
          <h4 className="text-sm font-semibold text-slate-900 mb-2">Recent Commands</h4>
          <div className="space-y-2 max-h-48 overflow-y-auto">
            {history.slice(-5).reverse().map((entry, index) => (
              <div
                key={index}
                className="p-3 rounded-lg border border-slate-200 bg-slate-50 text-sm"
              >
                <div className="flex items-center gap-2 mb-1">
                  <span className="font-medium text-slate-900">"{entry.input}"</span>
                  <span className="text-xs text-slate-500">
                    → {entry.command.intent} ({Math.round(entry.command.confidence * 100)}%)
                  </span>
                </div>
                {entry.result && (
                  <div className="text-xs text-slate-600 mt-1">
                    {typeof entry.result === "string" 
                      ? entry.result 
                      : entry.result.message || "Command executed"}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

"use client";

import React, { useState, useMemo } from "react";
import { Settings, Save, X, Check } from "lucide-react";

interface TemplateField {
  id: string;
  label: string;
  type: "text" | "number" | "select" | "toggle" | "multiselect";
  defaultValue?: any;
  options?: string[];
  description?: string;
  required?: boolean;
}

interface TemplateCustomizerProps {
  template: {
    id: string;
    title: string;
    fields?: TemplateField[];
  };
  onSave?: (customizations: Record<string, any>) => void;
  onCancel?: () => void;
  initialValues?: Record<string, any>;
}

export function TemplateCustomizer({
  template,
  onSave,
  onCancel,
  initialValues = {}
}: TemplateCustomizerProps) {
  const [values, setValues] = useState<Record<string, any>>(initialValues);
  const [errors, setErrors] = useState<Record<string, string>>({});

  const fields = template.fields || [];

  const handleChange = (fieldId: string, value: any) => {
    setValues(prev => ({ ...prev, [fieldId]: value }));
    // Clear error for this field
    if (errors[fieldId]) {
      setErrors(prev => {
        const next = { ...prev };
        delete next[fieldId];
        return next;
      });
    }
  };

  const validate = () => {
    const newErrors: Record<string, string> = {};
    fields.forEach(field => {
      if (field.required && (values[field.id] === undefined || values[field.id] === "" || 
          (Array.isArray(values[field.id]) && values[field.id].length === 0))) {
        newErrors[field.id] = `${field.label} is required`;
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = () => {
    if (validate() && onSave) {
      onSave(values);
    }
  };

  const renderField = (field: TemplateField) => {
    const value = values[field.id] ?? field.defaultValue;
    const error = errors[field.id];

    switch (field.type) {
      case "text":
        return (
          <div key={field.id} className="space-y-1">
            <label className="block text-sm font-medium text-slate-700">
              {field.label}
              {field.required && <span className="text-rose-600 ml-1">*</span>}
            </label>
            <input
              type="text"
              value={value || ""}
              onChange={(e) => handleChange(field.id, e.target.value)}
              className={`w-full px-3 py-2 rounded-lg border ${
                error ? "border-rose-300" : "border-slate-200"
              } focus:outline-none focus:ring-2 focus:ring-sky-500`}
            />
            {field.description && (
              <p className="text-xs text-slate-500">{field.description}</p>
            )}
            {error && <p className="text-xs text-rose-600">{error}</p>}
          </div>
        );

      case "number":
        return (
          <div key={field.id} className="space-y-1">
            <label className="block text-sm font-medium text-slate-700">
              {field.label}
              {field.required && <span className="text-rose-600 ml-1">*</span>}
            </label>
            <input
              type="number"
              value={value || ""}
              onChange={(e) => handleChange(field.id, Number(e.target.value))}
              className={`w-full px-3 py-2 rounded-lg border ${
                error ? "border-rose-300" : "border-slate-200"
              } focus:outline-none focus:ring-2 focus:ring-sky-500`}
            />
            {field.description && (
              <p className="text-xs text-slate-500">{field.description}</p>
            )}
            {error && <p className="text-xs text-rose-600">{error}</p>}
          </div>
        );

      case "select":
        return (
          <div key={field.id} className="space-y-1">
            <label className="block text-sm font-medium text-slate-700">
              {field.label}
              {field.required && <span className="text-rose-600 ml-1">*</span>}
            </label>
            <select
              value={value || ""}
              onChange={(e) => handleChange(field.id, e.target.value)}
              className={`w-full px-3 py-2 rounded-lg border ${
                error ? "border-rose-300" : "border-slate-200"
              } focus:outline-none focus:ring-2 focus:ring-sky-500`}
            >
              <option value="">Select...</option>
              {field.options?.map(opt => (
                <option key={opt} value={opt}>{opt}</option>
              ))}
            </select>
            {field.description && (
              <p className="text-xs text-slate-500">{field.description}</p>
            )}
            {error && <p className="text-xs text-rose-600">{error}</p>}
          </div>
        );

      case "toggle":
        return (
          <div key={field.id} className="flex items-center justify-between">
            <div>
              <label className="block text-sm font-medium text-slate-700">
                {field.label}
                {field.required && <span className="text-rose-600 ml-1">*</span>}
              </label>
              {field.description && (
                <p className="text-xs text-slate-500 mt-1">{field.description}</p>
              )}
            </div>
            <button
              type="button"
              onClick={() => handleChange(field.id, !value)}
              className={`relative w-12 h-6 rounded-full transition-colors ${
                value ? "bg-sky-600" : "bg-slate-300"
              }`}
            >
              <span
                className={`absolute top-1 left-1 w-4 h-4 bg-white rounded-full transition-transform ${
                  value ? "translate-x-6" : "translate-x-0"
                }`}
              />
            </button>
          </div>
        );

      case "multiselect":
        return (
          <div key={field.id} className="space-y-1">
            <label className="block text-sm font-medium text-slate-700">
              {field.label}
              {field.required && <span className="text-rose-600 ml-1">*</span>}
            </label>
            <div className="space-y-2">
              {field.options?.map(opt => {
                const isSelected = (value || []).includes(opt);
                return (
                  <label key={opt} className="flex items-center gap-2 cursor-pointer">
                    <input
                      type="checkbox"
                      checked={isSelected}
                      onChange={(e) => {
                        const current = value || [];
                        if (e.target.checked) {
                          handleChange(field.id, [...current, opt]);
                        } else {
                          handleChange(field.id, current.filter((v: string) => v !== opt));
                        }
                      }}
                      className="w-4 h-4 text-sky-600 rounded border-slate-300 focus:ring-sky-500"
                    />
                    <span className="text-sm text-slate-700">{opt}</span>
                  </label>
                );
              })}
            </div>
            {field.description && (
              <p className="text-xs text-slate-500">{field.description}</p>
            )}
            {error && <p className="text-xs text-rose-600">{error}</p>}
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="rounded-2xl border border-slate-200 bg-white p-6 shadow-sm">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-2">
          <Settings className="w-5 h-5 text-slate-600" />
          <h2 className="text-xl font-semibold text-slate-900">Customize Template</h2>
        </div>
        {onCancel && (
          <button
            onClick={onCancel}
            className="text-slate-600 hover:text-slate-900"
            aria-label="Close"
          >
            <X className="w-5 h-5" />
          </button>
        )}
      </div>

      <div className="mb-4">
        <h3 className="font-semibold text-slate-900">{template.title}</h3>
        <p className="text-sm text-slate-600 mt-1">
          Configure this template before starting. All fields marked with * are required.
        </p>
      </div>

      <div className="space-y-4">
        {fields.map(field => renderField(field))}
      </div>

      {fields.length === 0 && (
        <div className="text-center py-8 text-slate-600">
          <p>This template has no customization options.</p>
          <p className="text-sm mt-2">Click "Start Template" to begin with default settings.</p>
        </div>
      )}

      <div className="flex gap-3 mt-6 pt-6 border-t border-slate-200">
        <button
          onClick={handleSave}
          disabled={fields.length === 0}
          className="flex-1 px-4 py-2 bg-sky-600 text-white rounded-lg font-medium hover:bg-sky-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
        >
          <Save className="w-4 h-4" />
          Save & Start Template
        </button>
        {onCancel && (
          <button
            onClick={onCancel}
            className="px-4 py-2 border border-slate-300 text-slate-700 rounded-lg font-medium hover:bg-slate-50 transition-colors"
          >
            Cancel
          </button>
        )}
      </div>
    </div>
  );
}

/**
 * Memory Palace - Main Game Component
 */

"use client";

import React, { useState, useCallback, useEffect } from "react";
import GameShell from "@/lib/games/framework/GameShell";
import type { GameConfig, GameStatus } from "@/lib/games/framework/types";
import type { MemoryChallenge, ReviewResult } from "./types";
import { generateChallenge } from "./challengeGenerator";
import {
  getOrGenerateTodayChallenge,
  completeTodayChallenge,
} from "@/lib/games/shared/dailyChallenges";
import {
  generateTodayChallengeCode,
  storeChallengeCode,
} from "@/lib/games/shared/challengeCodes";
import {
  updateStreakForGame,
  getStreakDataForGame,
} from "@/lib/games/shared/streakTracking";
import {
  checkAndUnlockAchievements,
} from "@/lib/games/shared/achievements";
import {
  submitScore,
} from "@/lib/games/shared/scoreComparison";
import {
  ChallengeCodeShare,
  ScoreComparisonDisplay,
  AchievementDisplay,
  LeaderboardView,
} from "@/lib/games/shared/components";
import type { ChallengeCode } from "@/lib/games/shared/challengeCodes/types";

const GAME_CONFIG: GameConfig = {
  id: "memory-palace",
  title: "Memory Palace",
  description: "Train your memory with proven techniques",
  category: "educational",
  modes: ["solo", "daily"],
  supportsMultiplayer: false,
  minPlayers: 1,
  maxPlayers: 1,
  estimatedMinutes: 10,
  tutorialAvailable: true,
};

const GAME_ID = "memory-palace";

export default function MemoryPalace() {
  const [status, setStatus] = useState<GameStatus>("idle");
  const [challenge, setChallenge] = useState<MemoryChallenge | null>(null);
  const [memorizationPhase, setMemorizationPhase] = useState(true);
  const [recalledItems, setRecalledItems] = useState<Set<string>>(new Set());
  const [finalScore, setFinalScore] = useState<number | null>(null);
  const [startTime, setStartTime] = useState<number | null>(null);
  const [studyTime, setStudyTime] = useState<number>(0);
  const [challengeCodeData, setChallengeCodeData] = useState<ChallengeCode | null>(null);
  const [scoreComparison, setScoreComparison] = useState<any>(null);
  const [achievementIds, setAchievementIds] = useState<string[]>([]);
  const [playerScoreData, setPlayerScoreData] = useState<any>(null);

  useEffect(() => {
    if (status === "idle" && !challenge) {
      initializeChallenge();
    }
  }, [status, challenge]);

  useEffect(() => {
    let interval: NodeJS.Timeout | null = null;
    if (status === "playing" && memorizationPhase && startTime) {
      interval = setInterval(() => {
        setStudyTime(Math.floor((Date.now() - startTime) / 1000));
      }, 1000);
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [status, memorizationPhase, startTime]);

  const initializeChallenge = useCallback(() => {
    try {
      const dailyChallenge = getOrGenerateTodayChallenge(GAME_ID);
      const generated = generateChallenge({
        seed: dailyChallenge.seed,
        difficulty: "learner",
      });
      setChallenge(generated);
      setMemorizationPhase(true);
      setRecalledItems(new Set());
      setFinalScore(null);
      setStudyTime(0);
    } catch (error) {
      console.error("Error initializing challenge:", error);
    }
  }, []);

  const handleStartRecall = useCallback(() => {
    setMemorizationPhase(false);
  }, []);

  const handleItemRecall = useCallback((itemId: string) => {
    if (status !== "playing" || memorizationPhase) return;

    setRecalledItems(prev => {
      const next = new Set(prev);
      if (next.has(itemId)) {
        next.delete(itemId);
      } else {
        next.add(itemId);
      }
      return next;
    });
  }, [status, memorizationPhase]);

  const calculateScore = useCallback((): number => {
    if (!challenge) return 0;

    let correct = 0;
    for (const item of challenge.items) {
      if (recalledItems.has(item.id)) {
        correct++;
      }
    }

    const accuracy = (correct / challenge.items.length) * 100;
    return accuracy;
  }, [challenge, recalledItems]);

  const handleSubmit = useCallback(() => {
    if (!challenge || !startTime) return;

    const score = calculateScore();
    setFinalScore(score);
    setStatus("finished");

    const codeData = generateTodayChallengeCode(GAME_ID, challenge.seed);
    storeChallengeCode(codeData);
    setChallengeCodeData(codeData);

    const timeUsed = Date.now() - startTime;
    const scoreData = {
      score,
      time: timeUsed,
      scoreValue: score,
      timestamp: Date.now(),
    };

    setPlayerScoreData(scoreData);
    const comparison = submitScore(codeData.code, scoreData);
    setScoreComparison(comparison);

    completeTodayChallenge(GAME_ID, {
      score,
      time: timeUsed,
      scoreValue: score,
    });

    updateStreakForGame(GAME_ID, true);

    const streakData = getStreakDataForGame(GAME_ID);
    const achievementResult = checkAndUnlockAchievements({
      gameId: GAME_ID,
      currentStreak: streakData.currentStreak,
      gamesCompleted: 1,
    });
    setAchievementIds(achievementResult.newlyUnlocked);
  }, [challenge, startTime, calculateScore]);

  const handleStart = useCallback(() => {
    if (!challenge) {
      initializeChallenge();
      return;
    }
    setStatus("playing");
    setMemorizationPhase(true);
    setRecalledItems(new Set());
    setFinalScore(null);
    setStartTime(Date.now());
    setStudyTime(0);
  }, [challenge, initializeChallenge]);

  const handleReset = useCallback(() => {
    setStatus("idle");
    setMemorizationPhase(true);
    setRecalledItems(new Set());
    setFinalScore(null);
    setStartTime(null);
    setStudyTime(0);
    setChallenge(null);
    setChallengeCodeData(null);
    setScoreComparison(null);
    setAchievementIds([]);
    setPlayerScoreData(null);
  }, []);

  const rightPanel = challenge ? (
    <div className="space-y-4">
      <div>
        <h3 className="text-sm font-semibold text-slate-900 mb-2">Technique</h3>
        <div className="text-sm text-slate-600 capitalize">
          {challenge.technique}
        </div>
      </div>
      {memorizationPhase && (
        <div>
          <h3 className="text-sm font-semibold text-slate-900 mb-2">Study Time</h3>
          <div className="text-lg font-bold">
            {Math.floor(studyTime / 60)}:{(studyTime % 60).toString().padStart(2, '0')}
          </div>
        </div>
      )}
      {!memorizationPhase && (
        <div>
          <h3 className="text-sm font-semibold text-slate-900 mb-2">Recalled</h3>
          <div className="text-lg font-bold">
            {recalledItems.size} / {challenge.items.length}
          </div>
        </div>
      )}
      {finalScore !== null && (
        <div>
          <h3 className="text-sm font-semibold text-slate-900 mb-2">Score</h3>
          <div className="text-2xl font-bold text-green-600">
            {finalScore.toFixed(1)}%
          </div>
        </div>
      )}
    </div>
  ) : null;

  return (
    <GameShell
      config={GAME_CONFIG}
      status={status}
      onStart={handleStart}
      onReset={handleReset}
      rightPanel={rightPanel}
    >
      <div className="p-4">
        {!challenge ? (
          <div className="text-center text-slate-600">Loading challenge...</div>
        ) : status === "idle" ? (
          <div className="max-w-2xl mx-auto space-y-4">
            <h2 className="text-2xl font-bold text-slate-900">{challenge.name}</h2>
            <p className="text-slate-600">{challenge.description}</p>
            <button
              onClick={handleStart}
              className="w-full px-4 py-2 bg-amber-600 text-white rounded-lg hover:bg-amber-700"
            >
              Start Challenge
            </button>
          </div>
        ) : status === "playing" || status === "finished" ? (
          <div className="max-w-4xl mx-auto space-y-6">
            {memorizationPhase ? (
              <>
                <div>
                  <h3 className="text-lg font-semibold text-slate-900 mb-3">
                    Memorize these items ({challenge.technique})
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {challenge.items.map(item => (
                      <div
                        key={item.id}
                        className="p-4 border-2 border-slate-300 rounded-lg bg-white"
                      >
                        <div className="text-sm text-slate-500 mb-1">{item.type}</div>
                        <div className="text-lg font-semibold text-slate-900">
                          {item.content}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
                <button
                  onClick={handleStartRecall}
                  className="w-full px-4 py-3 bg-amber-600 text-white rounded-lg hover:bg-amber-700"
                >
                  I'm Ready to Recall
                </button>
              </>
            ) : (
              <>
                <div>
                  <h3 className="text-lg font-semibold text-slate-900 mb-3">
                    Select the items you remember
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {challenge.items.map(item => {
                      const isRecalled = recalledItems.has(item.id);
                      return (
                        <button
                          key={item.id}
                          onClick={() => handleItemRecall(item.id)}
                          disabled={status === "finished"}
                          className={`p-4 border-2 rounded-lg text-left transition-colors ${
                            isRecalled
                              ? "border-amber-600 bg-amber-100"
                              : "border-slate-300 bg-white hover:border-amber-400"
                          }`}
                        >
                          <div className="text-sm text-slate-500 mb-1">{item.type}</div>
                          <div className="text-lg font-semibold text-slate-900">
                            {item.content}
                          </div>
                        </button>
                      );
                    })}
                  </div>
                </div>
                <button
                  onClick={handleSubmit}
                  className="w-full px-4 py-3 bg-amber-600 text-white rounded-lg hover:bg-amber-700"
                >
                  Submit Recall
                </button>
              </>
            )}

            {/* Results */}
            {status === "finished" && finalScore !== null && challengeCodeData && (
              <div className="space-y-6">
                <div className="text-center">
                  <div className="text-3xl font-bold text-amber-600 mb-2">
                    Score: {finalScore.toFixed(1)}%
                  </div>
                </div>

                {/* Challenge Code Share */}
                <ChallengeCodeShare code={challengeCodeData} score={finalScore} />

                {/* Score Comparison */}
                {scoreComparison && (
                  <div className="rounded-xl border border-slate-200 bg-white p-4">
                    <ScoreComparisonDisplay comparison={scoreComparison} />
                  </div>
                )}

                {/* Leaderboard */}
                <div className="rounded-xl border border-slate-200 bg-white p-4">
                  <LeaderboardView 
                    challengeCode={challengeCodeData.code}
                    playerScore={playerScoreData || undefined}
                  />
                </div>

                {/* Achievements */}
                {achievementIds.length > 0 && (
                  <div className="rounded-xl border border-slate-200 bg-white p-4">
                    <AchievementDisplay achievementIds={achievementIds} compact />
                  </div>
                )}

                <button
                  onClick={handleReset}
                  className="w-full px-4 py-2 bg-amber-600 text-white rounded-lg hover:bg-amber-700"
                >
                  New Challenge
                </button>
              </div>
            )}
          </div>
        ) : null}
      </div>
    </GameShell>
  );
}

/**
 * Archive Replay Component
 * 
 * Handles replaying archived game runs using challenge codes.
 */

"use client";

import React, { useCallback } from "react";
import { Play, X } from "lucide-react";
import { loadChallengeFromCode } from "../challengeCodes/challengeCodeLoader";
import type { ArchivedRun } from "./ArchiveBrowser";

interface ArchiveReplayProps {
  archivedRun: ArchivedRun;
  onReplay: (challengeCode: string, seed: number) => void;
  onCancel?: () => void;
}

export function ArchiveReplay({
  archivedRun,
  onReplay,
  onCancel,
}: ArchiveReplayProps) {
  const handleReplay = useCallback(() => {
    if (!archivedRun.challengeCode) {
      // If no challenge code, we can't replay
      alert("This archived run doesn't have a challenge code and cannot be replayed.");
      return;
    }

    const challengeData = loadChallengeFromCode(archivedRun.challengeCode);
    if (!challengeData) {
      alert("Could not load challenge data. The challenge may no longer be available.");
      return;
    }

    onReplay(archivedRun.challengeCode, challengeData.seed);
  }, [archivedRun, onReplay]);

  if (!archivedRun.challengeCode) {
    return (
      <div className="rounded-lg border border-yellow-200 bg-yellow-50 p-4">
        <p className="text-sm text-yellow-800">
          This archived run cannot be replayed because it doesn't have a challenge code.
        </p>
      </div>
    );
  }

  return (
    <div className="rounded-lg border border-blue-200 bg-blue-50 p-4">
      <div className="flex items-center justify-between mb-3">
        <h3 className="text-sm font-semibold text-blue-900">Replay Archived Run</h3>
        {onCancel && (
          <button
            onClick={onCancel}
            className="p-1 hover:bg-blue-100 rounded transition-colors"
            aria-label="Cancel"
          >
            <X className="h-4 w-4 text-blue-600" />
          </button>
        )}
      </div>
      <p className="text-sm text-blue-800 mb-4">
        Replay this challenge using challenge code: <code className="font-mono text-xs bg-blue-100 px-1 rounded">{archivedRun.challengeCode}</code>
      </p>
      <button
        onClick={handleReplay}
        className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
      >
        <Play className="h-4 w-4" />
        Start Replay
      </button>
    </div>
  );
}

